parameters.functionCode:
// Validate API Key from header
const apiKey = $input.item.json.$request.headers['x-api-key'];
const expectedKey = $env.WEBHOOK_API_KEY;

if (!apiKey || apiKey !== expectedKey) {
  throw new Error('Unauthorized - Invalid API key');
}

// Validate required fields
const data = $input.item.json.body;
const errors = [];

// Email validation
if (!data.email || typeof data.email !== 'string') {
  errors.push('Email is required');
} else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email) || data.email.length > 255) {
  errors.push('Invalid email address');
}

// Subject validation
if (!data.subject || typeof data.subject !== 'string' || data.subject.trim().length === 0) {
  errors.push('Subject is required');
} else if (data.subject.length > 200) {
  errors.push('Subject must be less than 200 characters');
}

// Message validation
if (!data.message || typeof data.message !== 'string' || data.message.trim().length === 0) {
  errors.push('Message is required');
} else if (data.message.length > 10000) {
  errors.push('Message must be less than 10000 characters');
}

if (errors.length > 0) {
  throw new Error('Validation failed: ' + errors.join(', '));
}

// Sanitize inputs
return {
  email: data.email.trim().toLowerCase().substring(0, 255),
  subject: data.subject.trim().substring(0, 200),
  message: data.message.trim().substring(0, 10000)
};

