// Validate API Key from header
const apiKey = $input.item.json.$request.headers['x-api-key'];
const expectedKey = $env.WEBHOOK_API_KEY;

if (!apiKey || apiKey !== expectedKey) {
  throw new Error('Unauthorized - Invalid API key');
}

// Validate required fields
const data = $input.item.json.body;
const errors = [];

// Device model validation
if (!data.device_model || typeof data.device_model !== 'string' || data.device_model.trim().length === 0) {
  errors.push('Device model is required');
} else if (data.device_model.length > 100) {
  errors.push('Device model must be less than 100 characters');
}

// Serial number validation
if (!data.serial_number || typeof data.serial_number !== 'string' || data.serial_number.trim().length === 0) {
  errors.push('Serial number is required');
} else if (data.serial_number.length > 50) {
  errors.push('Serial number must be less than 50 characters');
}

// Error code validation (optional)
if (data.error_code && (typeof data.error_code !== 'string' || data.error_code.length > 50)) {
  errors.push('Invalid error code format');
}

// Description validation
if (!data.description || typeof data.description !== 'string' || data.description.trim().length === 0) {
  errors.push('Description is required');
} else if (data.description.length > 5000) {
  errors.push('Description must be less than 5000 characters');
}

// Event date validation
if (!data.event_date || typeof data.event_date !== 'string') {
  errors.push('Event date is required');
} else if (!/^\d{4}-\d{2}-\d{2}/.test(data.event_date)) {
  errors.push('Invalid event date format (use ISO 8601)');
}

if (errors.length > 0) {
  throw new Error('Validation failed: ' + errors.join(', '));
}

// Sanitize inputs
return {
  device_model: data.device_model.trim().substring(0, 100),
  serial_number: data.serial_number.trim().substring(0, 50),
  error_code: data.error_code ? data.error_code.trim().substring(0, 50) : '',
  description: data.description.trim().substring(0, 5000),
  event_date: data.event_date.trim()
};

